import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
outs = []
for s in data:
    # must be of form a^x b^y c^z with x>0,y>0,z>0 and z==x or z==y
    n = len(s)
    i = 0
    while i < n and s[i] == 'a':
        i += 1
    j = i
    while j < n and s[j] == 'b':
        j += 1
    k = j
    while k < n and s[k] == 'c':
        k += 1
    if k != n or i == 0 or j == i or k == j:
        outs.append("NO")
    else:
        x = i
        y = j - i
        z = k - j
        if z == x or z == y:
            outs.append("YES")
        else:
            outs.append("NO")
print("\n".join(outs))

