import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
n = data[0]
a = [0] * (n + 1)
for i in range(n):
    a[i+1] = data[1 + i]

if n == 1:
    print(1)
    sys.exit(0)

l = [1] * (n + 1)
r = [1] * (n + 2)

for i in range(2, n+1):
    if a[i] > a[i-1]:
        l[i] = l[i-1] + 1
    else:
        l[i] = 1

for i in range(n-1, 0, -1):
    if a[i] < a[i+1]:
        r[i] = r[i+1] + 1
    else:
        r[i] = 1

ans = max(l[1: n+1])

for i in range(1, n+1):
    if i == 1:
        ans = max(ans, 1 + r[2])
    elif i == n:
        ans = max(ans, l[n-1] + 1)
    else:
        if a[i+1] - a[i-1] >= 2:
            ans = max(ans, l[i-1] + 1 + r[i+1])
        else:
            ans = max(ans, l[i-1] + 1)
            ans = max(ans, r[i+1] + 1)

if ans > n:
    ans = n
print(ans)
