import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
n = data[0]
a = data[1:]
if n == 0:
    print(0)
    sys.exit(0)
if n == 1:
    print(1)
    sys.exit(0)

L = [1]*n
R = [1]*n

for i in range(1, n):
    if a[i] > a[i-1]:
        L[i] = L[i-1] + 1
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        R[i] = R[i+1] + 1

ans = max(L)  # without any change

for i in range(n):
    # change a[i]
    if i > 0:
        ans = max(ans, L[i-1] + 1)
    if i < n-1:
        ans = max(ans, R[i+1] + 1)
    if 0 < i < n-1 and a[i+1] - a[i-1] >= 2:
        ans = max(ans, L[i-1] + 1 + R[i+1])

if ans > n:
    ans = n
print(ans)

