import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
n = data[0]
a = data[1:1+n]

if n == 0:
    print(0)
    sys.exit(0)
if n == 1:
    print(1)
    sys.exit(0)

l = [1]*n
for i in range(1, n):
    if a[i] > a[i-1]:
        l[i] = l[i-1] + 1
    else:
        l[i] = 1

r = [1]*n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        r[i] = r[i+1] + 1
    else:
        r[i] = 1

ans = max(l)  # without any change

for i in range(n):
    if i == 0:
        ans = max(ans, 1 + (r[1] if n > 1 else 0))
    elif i == n-1:
        ans = max(ans, 1 + (l[n-2] if n > 1 else 0))
    else:
        ans = max(ans, l[i-1] + 1) 
        ans = max(ans, r[i+1] + 1)
        if a[i+1] - a[i-1] >= 2:
            ans = max(ans, l[i-1] + 1 + r[i+1])

if ans > n:
    ans = n
print(ans)

