import sys

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    a = [int(next(it)) for _ in range(n)]
    if n == 1:
        print(1)
        return
    incL = [1]*n
    for i in range(1,n):
        if a[i] > a[i-1]:
            incL[i] = incL[i-1] + 1
        else:
            incL[i] = 1
    incR = [1]*n
    for i in range(n-2, -1, -1):
        if a[i] < a[i+1]:
            incR[i] = incR[i+1] + 1
        else:
            incR[i] = 1
    ans = 1
    # without change
    for i in range(n):
        if incL[i] > ans:
            ans = incL[i]
    # try removing/changing one element
    for i in range(n):
        # change a[i]
        left = incL[i-1] if i-1 >= 0 else 0
        right = incR[i+1] if i+1 < n else 0
        # combine if possible by setting a[i] appropriately
        total = 1
        if i-1 >= 0:
            total = left + 1
        if i+1 < n:
            total = max(total, 1 + right)
        if 0 <= i-1 and i+1 < n and a[i+1] - a[i-1] >= 2:
            total = left + 1 + right
        # ensure not exceed n
        if total > n:
            total = n
        if total > ans:
            ans = total
    print(ans)

if __name__ == "__main__":
    main()
