import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
a = [int(next(it)) for _ in range(n)]

if n == 1:
    print(1)
    sys.exit(0)

l = [1]*n
for i in range(1,n):
    if a[i] > a[i-1]:
        l[i] = l[i-1]+1
    else:
        l[i] = 1

r = [1]*n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        r[i] = r[i+1]+1
    else:
        r[i] = 1

ans = max(l)  # at least longest already increasing
for i in range(n):
    # try remove/change a[i]
    left = l[i-1] if i-1 >= 0 else 0
    right = r[i+1] if i+1 < n else 0
    total = 1  # at least include this position (we can change it)
    # try to connect left and right by changing a[i]
    if i-1 >= 0 and i+1 < n:
        if a[i+1] - a[i-1] >= 2:
            total = left + 1 + right
        else:
            total = max(left+1, 1+right)
    else:
        total = max(left+1, 1+right)
    ans = max(ans, total)

# cannot exceed n
if ans > n:
    ans = n
print(ans)

