    t = nums[0]
    idx = 1
    for _ in range(t):
        pairs.append((nums[idx], nums[idx+1]))
        idx += 2
elif len(nums) % 2 == 0:
    for i in range(0, len(nums), 2):
        pairs.append((nums[i], nums[i+1]))
else:
    pairs.append((nums[0], nums[1]))

out_lines = []
for a, b in pairs:
    if a == b:
        out_lines.append("0")
        continue
    if a > b:
        a, b = b, a
    d = b - a
    best_lcm = (a * b)
    best_k = 0
    sq = int(math.isqrt(d))
    for i in range(1, sq + 1):
        if d % i == 0:
            for g in (i, d // i):
                r = a % g
                k = (g - r) % g
                A = a + k
                B = b + k
                # both divisible by g
                l = (A // g) * B
                if l < best_lcm or (l == best_lcm and k < best_k):
                    best_lcm = l
                    best_k = k
    out_lines.append(str(best_k))

sys.stdout.write("\n".join(out_lines))

