import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)

outs = []
if len(data) == 2:
    pairs = [tuple(data)]
else:
    pairs = []
    it = iter(data)
    for x in it:
        try:
            y = next(it)
        except StopIteration:
            break
        pairs.append((x, y))

for a, b in pairs:
    hours = a
    while a >= b:
        exchange = a // b
        hours += exchange
        a = exchange + (a % b)
    outs.append(str(hours))

sys.stdout.write("\n".join(outs))

