import sys

def longest_repeated_substring_length(s: str) -> int:
    n = len(s)
    for L in range(n-1, 0, -1):
        seen = set()
        for i in range(n - L + 1):
            sub = s[i:i+L]
            if sub in seen:
                return L
            seen.add(sub)
    return 0

