import sys

def minimize_ternary(s: str) -> str:
    cnt1 = s.count('1')
    t = s.replace('1', '')
    try:
        idx = t.index('2')
    except ValueError:
        idx = -1
    if idx == -1:
        return t + '1' * cnt1
    else:
        return t[:idx] + '1' * cnt1 + t[idx:]

def clean_token(tok: str) -> str:
    return ''.join(ch for ch in tok if ch in '012')

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    tokens = []
    for tok in data:
        cleaned = clean_token(tok)
        if cleaned:
            tokens.append(cleaned)
    # If no cleaned tokens (unlikely), try entire input as one string
    if not tokens:
        all_text = ''.join(ch for ch in sys.stdin.read() if ch in '012')
        if all_text:
            tokens = [all_text]
    results = [minimize_ternary(s) for s in tokens]
    print("\n".join(results))

