import sys
from collections import deque

def main():
    data = list(map(int, sys.stdin.read().split()))
    if not data:
        return
    it = iter(data)
    n = next(it)
    m = next(it)
    adj = [[] for _ in range(n)]
    for _ in range(m):
        a = next(it) - 1
        b = next(it) - 1
        adj[a].append(b)
        adj[b].append(a)

    color = [-1]*n
    comps = []  # list of (cnt0, cnt1)
    for i in range(n):
        if color[i] != -1:
            continue
        q = deque([i])
        color[i] = 0
        cnt0 = 0
        cnt1 = 0
        while q:
            u = q.popleft()
            if color[u] == 0:
                cnt0 += 1
            else:
                cnt1 += 1
            for v in adj[u]:
                if color[v] == -1:
                    color[v] = color[u]^1
                    q.append(v)
                elif color[v] == color[u]:
                    print("0 1")
                    return
        comps.append((cnt0, cnt1))

    # If any component has size >=3 (i.e., cnt0+cnt1 >=3) then t=1
    ways_t1 = 0
    has_size_ge3 = False
    for a,b in comps:
        if a + b >= 3:
            has_size_ge3 = True
        if a >= 2:
            ways_t1 += a*(a-1)//2
        if b >= 2:
            ways_t1 += b*(b-1)//2

    if has_size_ge3:
        print("1", ways_t1)
        return

    # All components are of size 1 or 2
    if m > 0:
        # t=2, ways = m * (n-2)
        print("2", m*(n-2))
        return
    else:
        # no edges at all
        if n < 3:
            print("0 0")
            return
        ways = n*(n-1)*(n-2)//6
        print("3", ways)

if __name__ == "__main__":
    main()
