from collections import deque

data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
it = iter(data)
n = next(it)
m = next(it)
adj = [[] for _ in range(n)]
for _ in range(m):
    a = next(it) - 1
    b = next(it) - 1
    adj[a].append(b)
    adj[b].append(a)

color = [-1]*n
sumPairs = 0
count_size2 = 0
has_nonbip = False

for i in range(n):
    if color[i] != -1:
        continue
    # BFS
    q = deque()
    q.append(i)
    color[i] = 0
    cnt0 = 0
    cnt1 = 0
    while q:
        u = q.popleft()
        if color[u] == 0:
            cnt0 += 1
        else:
            cnt1 += 1
        for v in adj[u]:
            if color[v] == -1:
                color[v] = color[u]^1
                q.append(v)
            elif color[v] == color[u]:
                has_nonbip = True
                break
        if has_nonbip:
            break
    if has_nonbip:
        break
    comp_size = cnt0 + cnt1
    sumPairs += cnt0*(cnt0-1)//2 + cnt1*(cnt1-1)//2
    if comp_size == 2:
        count_size2 += 1

if has_nonbip:
    print("0 1")
else:
    if sumPairs > 0:
        print("1", sumPairs)
    else:
        if m == 0:
            if n < 3:
                print("3 0")
            else:
                ways = n*(n-1)*(n-2)//6
                print("3", ways)
        else:
            # t = 2
            ways = count_size2 * (n - 2)
