import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
l = list(next(it).strip())
r = list(next(it).strip())

from collections import defaultdict, deque

left_pos = defaultdict(deque)
right_pos = defaultdict(deque)
left_q = deque()
right_q = deque()

for i, ch in enumerate(l, start=1):
    if ch == '?':
        left_q.append(i)
    else:
        left_pos[ch].append(i)
for i, ch in enumerate(r, start=1):
    if ch == '?':
        right_q.append(i)
    else:
        right_pos[ch].append(i)

pairs = []

# match same letters
for c in map(chr, range(ord('a'), ord('z')+1)):
    L = left_pos[c]
    R = right_pos[c]
    while L and R:
        pairs.append((L.popleft(), R.popleft()))

# match left specific to right '?'
for c in map(chr, range(ord('a'), ord('z')+1)):
    L = left_pos[c]
    while L and right_q:
        pairs.append((L.popleft(), right_q.popleft()))

# match left '?' to right specific
for c in map(chr, range(ord('a'), ord('z')+1)):
    R = right_pos[c]
    while R and left_q:
        pairs.append((left_q.popleft(), R.popleft()))

# match remaining '?' with '?'
while left_q and right_q:
    pairs.append((left_q.popleft(), right_q.popleft()))

out = []
out.append(str(len(pairs)))
for a, b in pairs:
    out.append(f"{a} {b}")
sys.stdout.write("\n".join(out))

