import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
it = iter(data)
n = next(it)
x = next(it)
segs = []
for _ in range(n):
    l = next(it); r = next(it); c = next(it)
    segs.append((l, r, c, r - l + 1))

segs_by_end = sorted(segs, key=lambda s: s[1])
segs_by_start = sorted(segs, key=lambda s: s[0])

INF = 10**30
best = [INF] * (x + 1)
p = 0
ans = INF

for l, r, c, length in segs_by_start:
    while p < n and segs_by_end[p][1] < l:
        _, _, cost_p, len_p = segs_by_end[p]
        if len_p <= x:
            if cost_p < best[len_p]:
                best[len_p] = cost_p
        p += 1
    if length <= x:
        need = x - length
        if need >= 1 and best[need] < INF:
            total = c + best[need]
            if total < ans:
                ans = total

if ans == INF:
    print(-1)
else:
    print(ans)

