                arr_end[t] = arr_end[t-1]
        # suffix min for start times: for t from limit down to 0, best cost with l >= t
        for t in range(limit, -1, -1):
            if arr_start[t+1] < arr_start[t]:
                arr_start[t] = arr_start[t+1]
        best_before[d] = arr_end
        best_after[d] = arr_start
