import sys, math

def is_snowflake(n: int) -> bool:
    if n < 7:
        return False
    # p is number of terms in geometric series: p >= 3
    # sum = 1 + k + k^2 + ... + k^(p-1)
    for p in range(3, 61):
        # approximate k as (n)^(1/(p-1))
        k_floor = int(n ** (1.0 / (p - 1)))
        if k_floor < 2:
            break
        # check neighborhood to account for floating precision
        for k in range(max(2, k_floor - 2), k_floor + 3):
            s = 1
            cur = 1
            for _ in range(1, p):
                cur *= k
                s += cur
                if s > n:
                    break
            if s == n:
                return True
    return False

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    t = int(data[0])
    res = []
    idx = 1
    for _ in range(t):
        n = int(data[idx]); idx += 1
        res.append("YES" if is_snowflake(n) else "NO")
