import sys
import math

def can_be_snowflake(n):
    if n < 7:
        return False
    # check m = 2 separately: n = 1 + k + k^2 -> k^2 + k + 1 - n = 0
    D = 4 * n - 3
    sd = math.isqrt(D)
    if sd * sd == D:
        if (sd - 1) % 2 == 0:
            k = (sd - 1) // 2
            if k >= 2:
                return True
    # for m >= 3
    max_m = int(math.log2(n))  # because 2^m <= n
    for m in range(3, max_m + 1):
        # approximate k
        k0 = int(n ** (1.0 / m))
        if k0 < 2:
            break
        lo = max(2, k0 - 2)
        hi = k0 + 2
        for k in range(lo, hi + 1):
            total = 1
            cur = 1
            for _ in range(m):
                cur *= k
                total += cur
                if total > n:
                    break
            if total == n:
                return True
    return False

