import sys

def feed_golorp(name):
    colon_pos = name.find(':')
    if colon_pos == -1:
        return 'false'
    num_vars = name[colon_pos + 1:].count('_')
    if num_vars <= 0:
        return 'false'
    return '0' * num_vars

if __name__ == '__main__':
    import sys
    input_str = sys.stdin.read().strip()
    print(feed_golorp(input_str))
