import sys
from itertools import product

def run_befunge(program, input_sequence):
    stack = []
    ip = 0
    input_idx = 0
    program = list(program)
    height = 1
    width = len(program)

    while ip < width:
        c = program[ip]
        if c == '>':
            ip += 1
        elif c == '<':
            ip -= 1
        elif c == '^':
            ip -= width
        elif c == 'v':
            ip += width
        elif c == '+':
            if len(stack) >= 2:
                b = stack.pop()
                a = stack.pop()
                stack.append(a + b)
            else:
                return False
        elif c == '-':
            if len(stack) >= 2:
                b = stack.pop()
                a = stack.pop()
                stack.append(a - b)
            else:
                return False
        elif c == '*':
            if len(stack) >= 2:
                b = stack.pop()
                a = stack.pop()
                stack.append(a * b)
            else:
                return False
        elif c == '/':
            if len(stack) >= 2:
                b = stack.pop()
                if b == 0:
                    return False
                a = stack.pop()
                stack.append(a // b)
            else:
                return False
        elif c == '%':
            if len(stack) >= 2:
                b = stack.pop()
                if b == 0:
                    return False
                a = stack.pop()
                stack.append(a % b)
            else:
                return False
        elif c == '!':
            if len(stack) >= 1:
                val = stack.pop()
                stack.append(1 if val == 0 else 0)
            else:
                return False
        elif c == '`':
            if len(stack) >= 2:
                b = stack.pop()
                a = stack.pop()
                stack.append(1 if a > b else 0)
            else:
                return False
        elif c == '_':
            if len(stack) >= 1:
                val = stack.pop()
                if val == 0:
                    ip -= 1
                else:
                    ip += 1
            else:
                return False
        elif c == '|':
            if len(stack) >= 1:
                val = stack.pop()
                if val == 0:
                    ip += 1
                else:
                    ip -= 1
            else:
                return False
        elif c == '"':
            # Read until next "
            end_idx = program.index('"', ip + 1)
            for i in range(ip + 1, end_idx):
                stack.append(ord(program[i]))
            ip = end_idx
        elif c == ',':
            if input_idx < len(input_sequence):
                stack.append(int(input_sequence[input_idx]))
                input_idx += 1
            else:
                return False
        elif c == '.':
            # Pop and print (not needed for validity)
            if stack:
                stack.pop()
            else:
                return False
        elif c == '#':
            ip += 2
        elif c == 'g':
            if len(stack) >= 2:
                y = stack.pop()
                x = stack.pop()
                if 0 <= x < width and 0 <= y < height:
                    stack.append(ord(program[x]))
                else:
                    return False
            else:
                return False
        elif c == 'p':
            if len(stack) >= 3:
                val = stack.pop()
                y = stack.pop()
                x = stack.pop()
                if 0 <= x < width and 0 <= y < height:
                    program[x] = chr(val)
                else:
                    return False
            else:
                return False
        elif c == '&':
            if input_idx < len(input_sequence):
                stack.append(int(input_sequence[input_idx]))
                input_idx += 1
            else:
                return False
        elif c == '@':
            return True
        else:
            # Ignore other characters
            ip += 1
    return False

def main():
    for line in sys.stdin:
        line = line.strip()
        if not line:
            continue
        # Extract the Befunge program part
        program_part = line.split('):-')[0].replace('?', '')
        program_part = program_part.replace('(', '').replace(')', '')
        # Count commas to determine the number of input variables
        num_vars = program_part.count(',')
        if num_vars == 0:
            print("false")
            continue
        # Generate all possible input sequences in lex order
        for seq in product('0123456789', repeat=num_vars):
            if run_befunge(line, ''.join(seq)):
                print(''.join(seq))
                break
        else:
            print("false")

if __name__ == "__main__":
    main()
