def note_to_index(note):
    notes = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "B", "H"]
    return notes.index(note)

def distance(x, y):
    return (y - x) % 12

def is_major(x, y, z):
    d1 = distance(x, y)
    d2 = distance(y, z)
    return d1 == 4 and d2 == 3

def is_minor(x, y, z):
    d1 = distance(x, y)
    d2 = distance(y, z)
    return d1 == 3 and d2 == 4

def classify_chord(notes):
    from itertools import permutations
    indices = [note_to_index(note) for note in notes]
    for perm in permutations(indices):
        x, y, z = perm
        if is_major(x, y, z):
            return "major"
        if is_minor(x, y, z):
            return "minor"
    return "strange"

def main():
    import sys
    input_line = sys.stdin.read().strip()
    notes = input_line.split()
    result = classify_chord(notes)
    print(result)

if __name__ == "__main__":
    main()
