import sys

def max_non_decreasing_subsequence_length(n, a):
    # Precompute the longest non-decreasing subsequence (LNDS) lengths
    # from left to right and right to left

    # dp_left[i] = length of LNDS ending at i
    dp_left = [1] * n
    for i in range(1, n):
        for j in range(i):
            if a[j] <= a[i]:
                dp_left[i] = max(dp_left[i], dp_left[j] + 1)

    # dp_right[i] = length of LNDS starting at i
    dp_right = [1] * n
    for i in range(n - 2, -1, -1):
        for j in range(i + 1, n):
            if a[i] <= a[j]:
                dp_right[i] = max(dp_right[i], dp_right[j] + 1)

    # Now try all possible reversals [l, r]
    max_len = 0
    for l in range(n):
        for r in range(l, n):
            # Reverse the subarray a[l:r+1]
            reversed_sub = a[l:r+1][::-1]
            # Create the new array
            new_a = a[:l] + reversed_sub + a[r+1:]
            # Compute the LNDS of the new array
            current_len = 1
            for i in range(1, n):
                if new_a[i-1] <= new_a[i]:
                    current_len += 1
            max_len = max(max_len, current_len)

