def calculate_hours(a, b):
    total_hours = 0
    while a > 0:
        # Burn all current candles
        total_hours += a
        # Convert used candles into new ones
        a = a // b
    return total_hours

import sys

def main():
    for line in sys.stdin:
        a, b = map(int, line.strip().split())
        print(calculate_hours(a, b))

if __name__ == "__main__":
    main()
