import threading

def main():
    import sys
    input = sys.stdin.read
    data = input().split('\n')

    idx = 0
    results = []

    while idx < len(data):
        if not data[idx]:
            idx += 1
            continue
        n = int(data[idx])
        idx += 1
        l = data[idx]
        idx += 1
        r = data[idx]
