        from collections import defaultdict

        left_color_count = defaultdict(int)
        right_color_count = defaultdict(int)
        left_q = []
        right_q = []

        for i in range(n):
            c = l[i]
            if c == '?':
                left_q.append(i)
            else:
                left_color_count[c] += 1

        for i in range(n):
            c = r[i]
            if c == '?':
                right_q.append(i)
            else:
                right_color_count[c] += 1

        # Pair specific colors with matching ones
        pairs = []
        used_left = set()
        used_right = set()

        # Match specific colors
        for color in left_color_count:
            if color in right_color_count:
                match_count = min(left_color_count[color], right_color_count[color])
                for i in range(match_count):
                    # Find the first unused index for left and right
                    for li in range(n):
                        if l[li] == color and li not in used_left:
                            used_left.add(li)
                            for ri in range(n):
                                if r[ri] == color and ri not in used_right:
                                    used_right.add(ri)
                                    pairs.append((li + 1, ri + 1))
                                    break
                            break

        # Match left '?' with right specific
        for li in left_q:
            if li in used_left:
                continue
            for ri in range(n):
                if r[ri] != '?' and ri not in used_right:
                    used_left.add(li)
                    used_right.add(ri)
                    pairs.append((li + 1, ri + 1))
                    break

        # Match right '?' with left specific
        for ri in right_q:
            if ri in used_right:
                continue
            for li in range(n):
                if l[li] != '?' and li not in used_left:
                    used_left.add(li)
                    used_right.add(ri)
                    pairs.append((li + 1, ri + 1))
                    break

        # Match remaining '?' with '?'
        left_q_remaining = [i for i in left_q if i not in used_left]
        right_q_remaining = [i for i in right_q if i not in used_right]

        match_count = min(len(left_q_remaining), len(right_q_remaining))
        for i in range(match_count):
            pairs.append((left_q_remaining[i] + 1, right_q_remaining[i] + 1))

        print(len(pairs))
        for a, b in pairs:
