import sys

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    idx = 0
    n = int(data[idx])
    idx += 1
    m = int(data[idx])
    idx += 1

    weight_cost = []
    weight1 = []
    weight2 = []
    weight3 = []

    for _ in range(n):
        w = int(data[idx])
        idx += 1
        c = int(data[idx])
        idx += 1
        if w == 1:
            weight1.append(c)
        elif w == 2:
            weight2.append(c)
        else:
            weight3.append(c)

    # Sort the lists to maximize cost
    weight1.sort(reverse=True)
    weight2.sort(reverse=True)
    weight3.sort(reverse=True)

    # Create a DP array where dp[i] is the maximum cost for weight i
    dp = [0] * (m + 1)

    # Fill dp using weight1
    for c in weight1:
        for i in range(m, 0, -1):
            if i >= 1:
                dp[i] = max(dp[i], dp[i - 1] + c)

    # Fill dp using weight2
    for c in weight2:
        for i in range(m, 0, -1):
            if i >= 2:
                dp[i] = max(dp[i], dp[i - 2] + c)

    # Fill dp using weight3
    for c in weight3:
        for i in range(m, 0, -1):
            if i >= 3:
                dp[i] = max(dp[i], dp[i - 3] + c)

    print(dp[m])

if __name__ == "__main__":
    main()
