def main():
    w, h, k = map(int, input().split())

    def border(n, m):
        if n < 1 or m < 1:
            return 0
        if n == 1 and m == 1:
            return 1
        if n == 1:
            return m
        if m == 1:
            return n
        return 2 * n + 2 * m - 4

    total = 0
    for i in range(1, k + 1):
        width_i = w - 4 * (i - 1)
        height_i = h - 4 * (i - 1)
        total += border(width_i, height_i)

    print(total)

