def main():
    import sys
    data = sys.stdin.read().split()
    n = int(data[0])
    l = float(data[1])
    ai_list = list(map(float, data[2:2+n]))

    ai_list.sort()

    max_d = 0.0

    if n == 0:
        print(l/2.0)
        return

    if ai_list[0] > 0:
        gap_start = ai_list[0] / 2.0
        if gap_start > max_d:
            max_d = gap_start

    for i in range(n-1):
        gap_between = (ai_list[i+1] - ai_list[i]) / 2.0
        if gap_between > max_d:
            max_d = gap_between

    if ai_list[-1] < l:
        gap_end = (l - ai_list[-1]) / 2.0
        if gap_end > max_d:
            max_d = gap_end

    print("{:.10f}".format(max_d))

if __name__ == "__main__":
