import sys

def main():
    data = sys.stdin.read().splitlines()
    n = int(data[0].strip())
    l_str = data[1].strip()
    r_str = data[2].strip()

    left_letter = []
    left_question = []
    for i, char in enumerate(l_str):
        if char != '?':
            left_letter.append((char, i+1))
        else:
            left_question.append(i+1)

    right_letter = []
    right_question = []
    for i, char in enumerate(r_str):
        if char != '?':
            right_letter.append((char, i+1))
        else:
            right_question.append(i+1)

    from collections import defaultdict
    freq_left = defaultdict(int)
    for char, idx in left_letter:
        freq_left[char] += 1

    freq_right = defaultdict(int)
    for char, idx in right_letter:
        freq_right[char] += 1

    pairs = []
    used_left = set()
    used_right = set()

    for char in freq_left:
        if char in freq_right:
            count_min = min(freq_left[char], freq_right[char])
            for _ in range(count_min):
                pairs.append((char, char))

    for char in freq_right:
        if char not in freq_left and char != '?':
            count_min = min(freq_left[char] if char in freq_left else 0, freq_right[char])
            if char in freq_left:
                count_min = min(freq_left[char], freq_right[char])
            else:
                count_min = freq_right[char]
            if count_min > 0:
                for _ in range(count_min):
                    pairs.append((char, '?'))

    for char in freq_left:
        if char != '?' and char not in freq_right:
            count_min = freq_left[char]
            for _ in range(count_min):
                pairs.append(('?', char))

    left_remaining = [idx for idx in left_question if idx not in used_right]
    right_remaining = [idx for idx in right_question if idx not in used_left]

    q_count = min(len(left_remaining), len(right_remaining))
    for i in range(q_count):
        pairs.append(('?', '?'))

    pairs.sort(key=lambda x: (x[0], x[1]) if x[0] != '?' else (0, x[1]))
    k = len(pairs)
