def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return

    n = int(data[0].strip())
    l_str = data[1].strip()
    r_str = data[2].strip()

    fixed_left_dict = defaultdict(list)
    wild_left_indices = []
    for i, char in enumerate(l_str):
        idx = i + 1
        if char == '?':
            wild_left_indices.append(idx)
        else:
            fixed_left_dict[char].append(idx)

    fixed_right_dict = defaultdict(list)
    wild_right_indices = []
    for i, char in enumerate(r_str):
        idx = i + 1
        if char == '?':
            wild_right_indices.append(idx)
        else:
            fixed_right_dict[char].append(idx)

    base_pairs = []
    all_colors = set(fixed_left_dict.keys()) | set(fixed_right_dict.keys())
    for c in all_colors:
        L_list = fixed_left_dict.get(c, [])
        R_list = fixed_right_dict.get(c, [])
        L_list.sort()
        R_list.sort()
        k = min(len(L_list), len(R_list))
        for i in range(k):
            base_pairs.append((L_list[i], R_list[i]))

    used_left_in_base = set()
    for left_idx, _ in base_pairs:
        used_left_in_base.add(left_idx)

    used_right_in_base = set()
    for _, right_idx in base_pairs:
        used_right_in_base.add(right_idx)

    unmatched_fixed_left = set(fixed_left_dict.keys()) & set(fixed_left_dict.keys())
    for c in fixed_left_dict:
        lst = fixed_left_dict[c]
        for idx in lst:
            if idx in used_left_in_base:
                unmatched_fixed_left.discard(idx)

    unmatched_fixed_right = set(fixed_right_dict.keys()) & set(fixed_right_dict.keys())
    for c in fixed_right_dict:
        lst = fixed_right_dict[c]
        for idx in lst:
            if idx in used_right_in_base:
                unmatched_fixed_right.discard(idx)

    unmatched_fixed_left_indices = []
    for c in fixed_left_dict:
        for idx in fixed_left_dict[c]:
            if idx not in used_left_in_base:
                unmatched_fixed_left_indices.append(idx)

    unmatched_fixed_right_indices = []
    for c in fixed_right_dict:
        for idx in fixed_right_dict[c]:
            if idx not in used_right_in_base:
                unmatched_fixed_right_indices.append(idx)

    unmatched_fixed_left_indices.sort()
    unmatched_fixed_right_indices.sort()
    wild_left_indices.sort()
    wild_right_indices.sort()

    x = min(len(unmatched_fixed_left_indices), len(wild_right_indices))
    type2_pairs = []
    for i in range(x):
        type2_pairs.append((unmatched_fixed_left_indices[i], wild_right_indices[i]))

    y = min(len(unmatched_fixed_right_indices), len(wild_left_indices))
    type3_pairs = []
    for i in range(y):
        type3_pairs.append((wild_left_indices[i], unmatched_fixed_right_indices[i]))

    remaining_wild_left = [idx for i, idx in enumerate(wild_left_indices) if i >= y]
    remaining_wild_right = [idx for i, idx in enumerate(wild_right_indices) if i >= x]

    z = min(len(remaining_wild_left), len(remaining_wild_right))
    type4_pairs = []
    for i in range(z):
        type4_pairs.append((remaining_wild_left[i], remaining_wild_right[i]))

    total_pairs = base_pairs + type2_pairs + type3_pairs + type4_pairs

    result = []
    for pair in total_pairs:
        a, b = pair
        result.append(f"{a} {b}")

    print(len(total_pairs))
    for line in result:
        print(line)

