from collections import defaultdict

def main():
    data = sys.stdin.read().split()
    n = int(data[0])
    x = int(data[1])
    vouchers = []
    index = 2
    for i in range(n):
        l = int(data[index])
        r = int(data[index+1])
        cost_val = int(data[index+2])
        index += 3
        vouchers.append((l, r, cost_val))

    max_day = 400000
    min_cost_by_duration = {}
    min_l_by_duration = {}
    max_r_by_duration = {}

    for l, r, cost_val in vouchers:
        d = r - l + 1
        if d not in min_cost_by_duration:
            min_cost_by_duration[d] = cost_val
            min_l_by_duration[d] = l
            max_r_by_duration[d] = r
        else:
            if cost_val < min_cost_by_duration[d]:
                min_cost_by_duration[d] = cost_val
                min_l_by_duration[d] = l
                max_r_by_duration[d] = r
            else:
                if l < min_l_by_duration[d]:
                    min_l_by_duration[d] = l
                if r > max_r_by_duration[d]:
                    max_r_by_duration[d] = r

    ans = float('inf')
    used = set()
    for d in list(min_cost_by_duration.keys()):
        d_other = x - d
        if d_other not in min_cost_by_duration or d in used:
            continue
        used.add(d)
        used.add(d_other)

        # Case 1: d voucher ends before d_other starts
        r1 = max_r_by_duration[d]
        l2_min = min_l_by_duration[d_other]
        if r1 < l2_min:
            total_cost = min_cost_by_duration[d] + min_cost_by_duration[d_other]
            if total_cost < ans:
                ans = total_cost

        # Case 2: d_other voucher ends before d voucher starts
        r2 = max_r_by_duration[d_other]
        l1_min = min_l_by_duration[d]
        if r2 < l1_min:
            total_cost = min_cost_by_duration[d] + min_cost_by_duration[d_other]
            if total_cost < ans:
                ans = total_cost

    if ans == float('inf'):
        print(-1)
    else:
        print(ans)

