        cost_val = int(data[index+2])
        index += 3
        vouchers.append((l, r, cost_val))

    max_day = 400000
    min_cost_dict = {}
    intervals_by_duration = defaultdict(list)

    for (l, r, cost_val) in vouchers:
        duration = r - l + 1
        if duration not in min_cost_dict:
            min_cost_dict[duration] = cost_val
            intervals_by_duration[duration].append((l, r))
        else:
            if cost_val < min_cost_dict[duration]:
                min_cost_dict[duration] = cost_val
            intervals_by_duration[duration].append((l, r))

    comp_duration = x - duration

    best_total = float('inf')
    found = False

    for dur, min_cost in min_cost_dict.items():
        comp = x - dur
        if comp not in min_cost_dict:
