        cost = int(data[idx+2])
        idx += 3
        vouchers.append((l, r, cost))

    min_total_cost = float('inf')

    # Group vouchers by their duration
    duration_map = {}
    for l, r, cost in vouchers:
        duration = r - l + 1
        if duration not in duration_map:
            duration_map[duration] = []
        duration_map[duration].append((l, r, cost))

    # For each duration d, check if x - d is also present
    for d in duration_map:
        if (x - d) not in duration_map:
            continue

        # Sort vouchers of duration d by end date
        vouchers_d = sorted(duration_map[d], key=lambda v: v[1])
        # Sort vouchers of duration x-d by start date
        vouchers_xd = sorted(duration_map[x - d], key=lambda v: v[0])

        # Use binary search to find non-overlapping pairs
        for l1, r1, cost1 in vouchers_d:
            # Find the earliest voucher in vouchers_xd that starts after r1
            start_idx = bisect.bisect_right(vouchers_xd, (r1 + 1, 0, 0), key=lambda v: v[0])
            for i in range(start_idx, len(vouchers_xd)):
                l2, r2, cost2 = vouchers_xd[i]
                if l2 > r1:
                    min_total_cost = min(min_total_cost, cost1 + cost2)

        # Also check the other way around
        for l1, r1, cost1 in vouchers_xd:
            start_idx = bisect.bisect_right(vouchers_d, (r1 + 1, 0, 0), key=lambda v: v[1])
            for i in range(start_idx, len(vouchers_d)):
