import math

def is_prime(x):
    if x < 2:
        return False
    if x == 2:
        return True
    if x % 2 == 0:
        return False
    i = 3
    while i * i <= x:
        if x % i == 0:
            return False
        i += 2
    return True

def factorize(n):
    factors = []
    d = n
    while d != 1:
        p = 2
        while p * p <= d:
            if d % p == 0:
                factors.append(p)
                d //= p
                break
            p += 1
        else:
            factors.append(d)
            break
    return factors

def get_all_factors(n):
    factors = set()
    if n == 1:
        return {1}
    for i in range(2, int(math.isqrt(n)) + 1):
        if n % i == 0:
            factors.add(i)
            factors.add(n // i)
    factors.add(n)
    return factors

