import math

def is_perfect_square(x):
    if x < 0:
        return False
    s = math.isqrt(x)
    return s * s == x

def solve_case(n):
    if n <= 2:
        return "NO"

    # Case 1: Two expansions (m=2)
    # n = 1 + k + k^2 -> k^2 + k + 1 - n = 0
    # Discriminant D = 1 - 4*(1-n) = 4*n - 3
    D = 4 * n - 3
    if is_perfect_square(D):
        s = math.isqrt(D)
        k = (s - 1) // 2
        if k > 1 and (k * k + k + 1 == n):
            return "YES"

    # Case 2: More than two expansions (m>=3)
    # Exponent e from 3 to 60 (inclusive)
    for e in range(3, 61):
        low = 2
        high = 2
        while True:
            terms = [pow(high, i) for i in range(e)]
            total_terms = sum(terms)
            if total_terms < n:
                high *= 2
            elif total_terms >= n:
                break
        else:
            continue

        found = False
        while low <= high:
            mid = (low + high) // 2
            try:
                total_val = (pow(mid, e) - 1) // (mid - 1)
            except ZeroDivisionError:
                continue
            if total_val == n:
                found = True
                break
            elif total_val < n:
                low = mid + 1
            else:
                high = mid - 1

        if found:
            return "YES"

    return "NO"

