sys.setrecursionlimit(300000)

def main():
    data = sys.stdin.read().split()
    it = iter(data)
    n = int(next(it))
    m = int(next(it))

    edges = []
    for _ in range(m):
        a = int(next(it))
        b = int(next(it))
        c = int(next(it))
        edges.append((a, b, c))

    parent = list(range(n+1))
    rank = [0] * (n+1)

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    for i in range(2, n+1):
        a, b, c = edges[i-1]
        ra = find(a)
        rb = find(b)
        if ra == rb:
            continue
        if rank[ra] < rank[rb]:
            parent[ra] = rb
        elif rank[ra] > rank[rb]:
            parent[rb] = ra
        else:
            parent[rb] = ra
            rank[ra] += 1

    result = []
    for i in range(m):
        a, b, c = edges[i]
        root_a = find(a)
        root_b = find(b)
        if root_a == root_b:
            if a == 1 or b == 1:
                result.append('0')
            else:
                result.append('1')
        else:
            if root_a == find(1):
                result.append('0')
            else:
                result.append('1')

    print("\n".join(result))

