    visited = [False] * (n+1)

    for i in range(1, n+1):
        if not visited[i]:
            stack = [i]
            visited[i] = True
            while stack:
                node = stack[-1]
                found = False
                for neighbor, cap, idx in graph[node]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        stack.append(neighbor)
                        found = True
                        break
                if not found:
                    topo_order.append(node)
                    stack.pop()

    flow_balance = [0] * (n+1)
