        edges.append((a, b, c))

    graph = [[] for _ in range(n+1)]
    for i, (a, b, c) in enumerate(edges):
        graph[a].append((b, c, i+1))
        graph[b].append((a, c, i+1))

    indegree = [0] * (n+1)
    for i in range(1, n+1):
        for neighbor, weight, idx in graph[i]:
            if i == 1:
                continue
            indegree[neighbor] += 1

    q = deque()
    for i in range(1, n+1):
        if indegree[i] == 0:
            q.append(i)

    topo_order = []
    while q:
        node = q.popleft()
        topo_order.append(node)
        for neighbor, weight, idx in graph[node]:
            indegree[neighbor] -= 1
