    b_list.sort(reverse=True)
    c_list.sort(reverse=True)

    len_a = len(a_list)
    len_b = len(b_list)
    len_c = len(c_list)

    prefix_a = [0]*(len_a+1)
    for i in range(1, len_a+1):
        prefix_a[i] = prefix_a[i-1] + a_list[i-1]

    prefix_b = [0]*(len_b+1)
    for i in range(1, len_b+1):
        prefix_b[i] = prefix_b[i-1] + b_list[i-1]

    prefix_c = [0]*(len_c+1)
    for i in range(1, len_c+1):
        prefix_c[i] = prefix_c[i-1] + c_list[i-1]

    best_ans = 0

