            base = prefix_b[y]
            for x in range(0, max_x - w_used_by_y + 1):
                if dp_arr[x + w_used_by_y] < base + prefix_a[x]:
                    dp_arr[x + w_used_by_y] = base + prefix_a[x]

        for x in range(0, max_x + 1):
