            candidate = current_base + prefix1[min(num_type1, L1)]
            if candidate > dp[w]:
                dp[w] = candidate
        for w in range(end_w+1, m_val+1):
            if dp[w] < current_base + prefix1[L1]:
                dp[w] = current_base + prefix1[L1]

    for z in range(0, max_z+1):
        rem = m_val - 3*z
        if rem < 0:
            break
        total_cost = dp[rem] + prefix3[z]
        if total_cost > best:
            best = total_cost

    print(best)

