import sys
from collections import defaultdict, deque

def main():
    data = sys.stdin.read().splitlines()
    results = []
    for line in data:
        if not line.strip():
            continue
        s = line.strip()
        tokens = s.split()
        n = len(tokens)
        if n < 2 or tokens[0] != '?(':
            results.append("false")
            continue

        var_tokens = []
        i = 1
        while i < len(tokens):
            if tokens[i] == ')':
                break
            if tokens[i] == ',':
                i += 1
                continue
            var_tokens.append(tokens[i])
            i += 1

        if not var_tokens:
            results.append("false")
            continue

        num_vars = len(var_tokens)
        conditions = []
        i += 1
        while i < len(tokens):
            if tokens[i] == ')':
                break
            if tokens[i] == ',':
                i += 1
                continue
            conditions.append(tokens[i])
            i += 1

        if not conditions:
            results.append("false")
            continue

        graph = defaultdict(list)
        in_degree = [0] * num_vars

        condition_index = 0
        for cond in conditions:
            parts = cond.split(':')
            if len(parts) < 2:
                continue

            var_name = parts[0].strip()
            rest = ':'.join(parts[1:]).strip()
            sub_parts = rest.split(':')
            if len(sub_parts) < 2:
                continue

            target_var = sub_parts[0].strip()
            expr = sub_parts[1].strip()
            try:
                value = int(expr)
            except:
                continue

            if var_name not in var_tokens or target_var not in var_tokens:
                continue

            idx1 = var_tokens.index(var_name)
            idx2 = var_tokens.index(target_var)
            graph[idx1].append((idx2, value))
            in_degree[idx2] += 1

        for i in range(num_vars):
            if in_degree[i] == 0:
                q = deque([i])
                topo_order = []
                while q:
                    node = q.popleft()
                    topo_order.append(node)
                    for neighbor, _ in graph[node]:
                        in_degree[neighbor] -= 1
                        if in_degree[neighbor] == 0:
                            q.append(neighbor)
                break
        else:
            results.append("false")
            continue

        if len(topo_order) != num_vars:
            results.append("false")
            continue

        current_assignment = {var_idx: None for var_idx in range(num_vars)}
        for idx in topo_order:
            if graph[idx]:
                prev_condition = graph[idx][0][1]
                candidate = prev_condition
                current_assignment[idx] = candidate
            else:
                current_assignment[idx] = 0

        out_sequence = ''.join(str(current_assignment[i]) for i in range(num_vars))
        results.append(out_sequence)

    for res in results:
        print(res)

