import sys

def parse_instructions(s):
    tokens = []
    i = 0
    n = len(s)
    while i < n:
        if s[i] == ' ':
            i += 1
            continue
        elif s[i] == '(':
            j = i + 1
            count = 1
            while j < n and count != 0:
                if s[j] == '(':
                    count += 1
                elif s[j] == ')':
                    count -= 1
                j += 1
            tokens.append(s[i+1:j-1])
            i = j
        elif s[i] == '[':
            j = i + 1
            count = 1
            while j < n and count != 0:
                if s[j] == '[':
                    count += 1
                elif s[j] == ']':
                    count -= 1
                j += 1
            tokens.append(s[i+1:j-1])
            i = j
        elif s[i] == '?':
            j = i + 1
            count = 1
            while j < n and count != 0:
                if s[j] == '(':
                    count += 1
                elif s[j] == ')':
                    count -= 1
                j += 1
            tokens.append(s[i+1:j-1])
            i = j
        else:
            tokens.append(s[i])
            i += 1
    return tokens

def process_tokens(tokens):
    variables = set()
    constraints = []
    current_var = None
    current_assign = None
    i = 0
    while i < len(tokens):
        token = tokens[i]
        if token == '?':
            if i + 1 < len(tokens) and tokens[i+1] in ['(', '[']:
                current_var = tokens[i+1]
                variables.add(current_var)
                i += 2
            else:
                i += 1
        elif token == '=':
            if i + 1 < len(tokens) and isinstance(tokens[i+1], int):
                constraints.append(('=', tokens[i+1]))
                i += 2
            else:
                i += 1
        elif token == '<':
            if i + 1 < len(tokens) and isinstance(tokens[i+1], int):
                constraints.append(('lt', tokens[i+1]))
                i += 2
            else:
                i += 1
        elif token == '-':
            if i + 1 < len(tokens) and isinstance(tokens[i+1], int):
                constraints.append(('dec', tokens[i+1]))
                i += 2
            else:
                i += 1
        elif token == '+':
            if i + 1 < len(tokens) and isinstance(tokens[i+1], int):
                constraints.append(('inc', tokens[i+1]))
                i += 2
            else:
                i += 1
        elif token in ['(', '[']:
            num = ''
            j = i + 1
            while j < len(tokens) and tokens[j] not in [')', ']', '='] and tokens[j] not in ['-', '+', '?']:
                num += tokens[j]
                j += 1
            if num != '':
                try:
                    num_val = int(num)
                    constraints.append(('<', num_val))
                    i = j
                except:
                    i += 1
            else:
                i += 1
        elif token.isdigit():
            num_val = int(token)
            constraints.append(('<', num_val))
            i += 1
        else:
            i += 1

    # Extract all variables from constraints
    for con in constraints:
        if isinstance(con[1], str):
            variables.add(con[1])
    return variables, constraints

def solve_golorp(variables, constraints):
    n = len(variables)
    var_list = sorted(variables)
    total_vars = len(var_list)
    assignments = {var: None for var in variables}
    min_vals = {var: 0 for var in variables}
    max_vals = {var: 9 for var in variables}

    def check_constraints(assignments, constraints):
        for con in constraints:
            if con[0] == '<':
                a_val = assignments.get(con[1], None)
                if a_val is None:
                    continue
                if a_val > con[1]:
                    return False
            elif con[0] == '=':
                a_val = assignments.get(con[1], None)
                if a_val is None:
                    continue
                if a_val != con[1]:
                    return False
            elif con[0] == 'lt':
                a_val = assignments.get(con[1], None)
                if a_val is None:
                    continue
                if a_val < con[1]:
                    return False
            elif con[0] == 'dec':
                a_val = assignments.get(con[1], None)
                if a_val is None:
                    continue
                if a_val - assignments.get(con[2], 0) != con[1]:
                    return False
            elif con[0] == 'inc':
                a_val = assignments.get(con[1], None)
                if a_val is None:
                    continue
                if a_val - assignments.get(con[2], 0) != con[1]:
                    return False
        return True

    def dfs(var_index):
        if var_index == total_vars:
            if check_constraints(assignments, constraints):
                return assignments
            return None

        var_name = var_list[var_index]
        for val in range(min_vals[var_name], 10):
            assignments[var_name] = val
            result = dfs(var_index + 1)
            if result is not None:
                return result
            assignments[var_name] = None
        return None

    return dfs(0)

def main():
    data = sys.stdin.read().splitlines()
    results = []
    for line in data:
        s = line.strip()
        if not s:
            continue
        tokens = parse_instructions(s)
        variables, constraints = process_tokens(tokens)
        solution = solve_golorp(variables, constraints)
        if solution is None:
            results.append("false")
        else:
            sol_str = ''.join(str(solution[var]) for var in var_list)
            results.append(sol_str)

    for res in results:
        print(res)

if __name__ == "__main__":
    main()

