        graph = {var: [] for var in var_list}
        in_degree = {var: 0 for var in var_list}
        rev_graph = {var: [] for var in var_list}
        constraint_map = {}

        for cons in constraints:
            a, op, b = cons
            if isinstance(a, str):
                a = int(a.split()[1])
            if isinstance(b, str):
                b = int(b.split()[1])

            if op == '>':
                constraint_map[(a, b)] = '>'
                rev_graph[b].append(a)
                graph[a].append(b)
                in_degree[b] += 1
            elif op == '<':
                constraint_map[(a, b)] = '<'
                rev_graph[b].append(a)
                graph[a].append(b)
                in_degree[b] += 1
            elif op == '=':
                constraint_map[(a, b)] = '='
                rev_graph[b].append(a)
                graph[a].append(b)
                in_degree[b] += 1

        for var in graph:
            for neighbor in graph[var]:
                if var not in rev_graph[neighbor]:
                    rev_graph[neighbor].append(var)

        for var in var_list:
            for neighbor in rev_graph[var]:
                if var not in graph[neighbor]:
                    graph[neighbor].append(var)

        q = deque()
        for var in var_list:
            if in_degree[var] == 0:
                q.append(var)

        topo_order = []
        while q:
            node = q.popleft()
            topo_order.append(node)
            for neighbor in graph[node]:
                in_degree[neighbor] -= 1
                if in_degree[neighbor] == 0:
                    q.append(neighbor)

        if len(topo_order) != n_vars:
            results.append("false")
            continue

        assignments = {}
        for var in reversed(topo_order):
            if var in constraint_map:
                continue
            min_digit = 0
            while min_digit < 10:
                if min_digit not in assignments.values():
                    assignments[var] = min_digit
                    min_digit += 1
                    break

        seq = ''.join(str(assignments[var]) for var in var_list)
        results.append(seq)

    for res in results:
        print(res)

