        constraints_dict[a] = b

    return jaws_pattern, constraints_dict

def is_valid_sequence(jaws_pattern, sequence):
    # Check if the sequence length matches the number of variables in jaws_pattern
    if len(sequence) != len(jaws_pattern):
        return False

