    for c in jaws_pattern:
        if c not in '()0123456789':
            return False

    # Check if the sequence contains only digits
    for c in sequence:
        if c not in '0123456789':
            return False

    return True

def find_lex_smallest_sequence(jaws_pattern, constraints):
    # Convert pattern to a list for easier manipulation
    pattern = list(jaws_pattern)
    n = len(pattern)

    # Initialize the sequence with '0's
    sequence = ['0'] * n

    # Use BFS to find the lexicographically smallest valid sequence
    queue = deque([sequence])
    visited = set()
    visited.add(tuple(sequence))

    while queue:
        current = queue.popleft()

        # Check if current sequence satisfies all constraints
        if all(satisfies_constraint(current, constraint, pattern) for constraint in constraints):
            return ''.join(current)

        # Try increasing the next variable
        for i in range(n):
            if pattern[i] == '(':
                continue
            if pattern[i] == ')':
                continue
            if current[i] == '9':
                continue
            new_seq = current[:]
            new_seq[i] = str(int(new_seq[i]) + 1)
            new_seq_tuple = tuple(new_seq)
            if new_seq_tuple not in visited:
                visited.add(new_seq_tuple)
                queue.append(new_seq)

