def satisfies_constraint(sequence, constraint, pattern):
    a, b = constraint.split('>')
    a_idx = -1
    b_idx = -1
    for i, c in enumerate(pattern):
        if c == a and a_idx == -1:
            a_idx = i
        if c == b and b_idx == -1:
            b_idx = i

    if a_idx == -1 or b_idx == -1:
        return False

    return int(sequence[a_idx]) < int(sequence[b_idx])

