            num_games = 1 << (n - current_round - 1)
            num_teams_in_round = total_teams >> current_round
            games_per_path = num_teams_in_round // 2

            required_opponents = set()
            for i in range(games_per_path):
                if i % 2 == 0:
                    left = (i << (current_round + 1)) + 1
                    right = left + 1
                    if (left & x_bit) or (right & x_bit):
                        continue
                    if s[current_round] == '1':
                        required_opponents.add(left)
                        required_opponents.add(right)
                    else:
                        required_opponents.add(right)
                        required_opponents.add(left)
                else:
                    left = (i << (current_round + 1)) + 1
                    right = left + 1
                    if (left & x_bit) or (right & x_bit):
