                    if s[current_round] == '1':
                        required_opponents.add(right)
                        required_opponents.add(left)
                    else:
                        required_opponents.add(left)
                        required_opponents.add(right)

            for opp in required_opponents:
                if (opp >> (n - current_round - 1)) & 1:
                    return False
            current_round += 1

