                else:
                    node['right'] = build_tree(phase + 1, min_val, max_val, left_count, right_count)
            else:
                if right_count > 0:
                    node['right'] = build_tree(phase + 1, min_val, max_val, left_count, right_count)
                else:
                    node['left'] = build_tree(phase + 1, min_val, max_val, left_count, right_count)

