        right_avail = set(range(1, total_teams + 1))
        left_avail.discard(x)
        right_avail.discard(x)

        def dfs(phase, left_set, right_set):
            nonlocal left_avail, right_avail
            if phase == n:
                return True

            required_size = 1 << (n - phase - 1)
            other_size = required_size

            if s_str[phase] == '1':
                if left_set and not right_set:
                    if len(left_set) < required_size:
                        return False
                    if max(left_set) < x:
                        return True
                    else:
                        return False
                elif right_set and not left_set:
                    if len(right_set) < required_size:
                        return False
                    if max(right_set) < x:
                        return True
                    else:
                        return False
                else:
                    if left_set and right_set:
                        L_winner = max(left_set)
                        R_winner = max(right_set)
                        if L_winner > R_winner:
                            winner = L_winner
                            new_left = left_set - {L_winner}
                            new_right = right_set
                        else:
                            winner = R_winner
                            new_left = left_set
                            new_right = right_set - {R_winner}
                    else:
                        return False
            else:  # '0'
                if left_set and not right_set:
                    if len(left_set) < required_size:
                        return False
                    if min(left_set) > x:
                        return True
                    else:
                        return False
                elif right_set and not left_set:
                    if len(right_set) < required_size:
                        return False
                    if min(right_set) > x:
                        return True
                    else:
                        return False
                else:
                    if left_set and right_set:
                        L_winner = min(left_set)
                        R_winner = min(right_set)
                        if L_winner < R_winner:
                            winner = L_winner
                            new_left = left_set - {L_winner}
                            new_right = right_set
                        else:
                            winner = R_winner
                            new_left = left_set
                            new_right = right_set - {R_winner}
                    else:
                        return False

            if phase < n - 1:
                next_phase = phase + 1
                size_next = 1 << (n - next_phase - 1)
                if winner in left_avail:
                    left_avail.remove(winner)
                    result = dfs(next_phase, left_avail, right_avail)
                    if result:
                        return True
                    left_avail.add(winner)
                if winner in right_avail:
                    right_avail.remove(winner)
                    result = dfs(next_phase, left_avail, right_avail)
                    if result:
                        return True
                    right_avail.add(winner)
            return False

        return dfs(0, set(), set())

    winning_integers = []
    for x in range(1, total_teams + 1):
